require("define")

local ShowMsg = {}

---@param warnMsgs CheckSingleConfigResult[]
function ShowMsg.Show(warnMsgs)

    --https://qyapi.weixin.qq.com/cgi-bin/webhook/send?key=0fe47b93-2a3a-45f9-b11c-eaff4efcd6db
    WeCom.SetKey("0fe47b93-2a3a-45f9-b11c-eaff4efcd6db")

    local count = 0
    for configName, configMsg in pairs(warnMsgs) do

        if configMsg:HasAnyMsg() then
            local msg = configMsg:ToString()
            local content = "#【地图资源检测工具】"..configName.."\n"
            content = content..msg
            print(content)
            WeCom.SendMarkDown(content)
        end
        count = count + 1
    end

    if count == 0 then
        local content = "【地图资源检测工具】检查完成 未发现任何问题~"
        print(content)
        WeCom.SendMarkDown(content)
    end
end

return ShowMsg